css:
.box {
  width: 90%;
  height: 90%;
  display: grid;
  place-items: center;
  background-color: #6fa6f9;
  transition-property: transform, box-shadow;
  transition-duration: .2s;
}

.box__text {
  font-family: verdana;
  font-size: 52px;
  font-weight: bold;
  color: #222;
}

.box:hover {
  transform: translate(-9px, -9px);
  box-shadow: 
    1px 1px #222, 2px 2px #222, 3px 3px #222, 
    4px 4px #222,5px 5px #222, 6px 6px #222, 
    7px 7px #222, 8px 8px #222, 9px 9px #222;
}


body {
	background-color: #070627;
}

.cards {
	width: 200px;
	height: 200px;
	margin: auto;
	position: relative;
	transform-style: preserve-3d;
	transform: rotateX(60deg) rotateZ(-45deg);
}

.cards__card {
	width: inherit;
	height: inherit;
	position: absolute;
	border-radius: 10px;
	transition: transform .3s;
}


.cards__card:nth-child(1) {
	background-color: #19187d;
}
.cards__card:nth-child(2) {
	background-color: #1f1da4;
	transform: translateZ(10px);
}
.cards__card:nth-child(3) {
	background-color: #2a27d7;
	transform: translateZ(20px);
}


.cards:hover .cards__card:nth-child(2) {
	transform: translateZ(40px);
}

.cards:hover .cards__card:nth-child(3) {
	transform: translateZ(80px);
}
